mapper.select:
SELECT BT_name
FROM BookType
WHERE BT_no=#{typeNo};

SELECT BT_no
FROM BookType
WHERE BT_name=#{typeName};

SELECT B_inventory
FROM BookInfo
WHERE B_no=#{isbn};

mapper.update:
UPDATE BookInfo
SET B_inventory=B_inventory-1
WHERE B_no=#{isbn};

UPDATE BorrowBook
SET BB_backTime=#{backTime}
WHERE BB_no=#{no};

UPDATE BookInfo
SET B_inventory=B_inventory+1
WHERE B_no=#{isbn};
