mapper.select:
SELECT M_password, M_memberNo, M_power
FROM Member
WHERE M_email=#{email};

SELECT M_power
FROM Member
WHERE M_memberNo = #{memberNo};

SELECT DT_no
FROM DegreeType
WHERE DT_name=#{degree};

mapper.update:
UPDATE Member
SET M_endTime = #{endTime}
WHERE M_memberNo = #{no};

UPDATE Member
M_stuNo = #{stuNo}, M_name = #{name}, M_phone = #{phone}, M_email = #{email}, M_gender = #{gender} M_discipline = #{discipline}, M_grade = #{grade}, M_class = #{classNum}, M_degreeNo = #{degreeNo}, M_id = #{id}, M_typeNo = #{memberTypeNo}, M_power = #{power}, M_addScore = #{addScore},
WHERE M_memberNo = #{no};
